#pragma once

#ifndef ITEMREMOVEDOBJECT_H
#define ITEMREMOVEDOBJECT_H

#include "MAPISchema.h"

namespace graphql::mapi::object {
namespace implements {

template <class I>
concept ItemRemovedIs = std::is_same_v<I, ItemChange>;

} // namespace implements

namespace methods::ItemRemovedHas {

template <class TImpl>
concept getIndexWithParams = requires (TImpl impl, service::FieldParams params)
{
	{ service::AwaitableScalar<int> { impl.getIndex(std::move(params)) } };
};

template <class TImpl>
concept getIndex = requires (TImpl impl)
{
	{ service::AwaitableScalar<int> { impl.getIndex() } };
};

template <class TImpl>
concept getRemovedWithParams = requires (TImpl impl, service::FieldParams params)
{
	{ service::AwaitableScalar<response::IdType> { impl.getRemoved(std::move(params)) } };
};

template <class TImpl>
concept getRemoved = requires (TImpl impl)
{
	{ service::AwaitableScalar<response::IdType> { impl.getRemoved() } };
};

template <class TImpl>
concept beginSelectionSet = requires (TImpl impl, const service::SelectionSetParams params)
{
	{ impl.beginSelectionSet(params) };
};

template <class TImpl>
concept endSelectionSet = requires (TImpl impl, const service::SelectionSetParams params)
{
	{ impl.endSelectionSet(params) };
};

} // namespace methods::ItemRemovedHas

class [[nodiscard("unnecessary construction")]] ItemRemoved final
	: public service::Object
{
private:
	[[nodiscard("unnecessary call")]] service::AwaitableResolver resolveIndex(service::ResolverParams&& params) const;
	[[nodiscard("unnecessary call")]] service::AwaitableResolver resolveRemoved(service::ResolverParams&& params) const;

	[[nodiscard("unnecessary call")]] service::AwaitableResolver resolve_typename(service::ResolverParams&& params) const;

	struct [[nodiscard("unnecessary construction")]] Concept
	{
		virtual ~Concept() = default;

		virtual void beginSelectionSet(const service::SelectionSetParams& params) const = 0;
		virtual void endSelectionSet(const service::SelectionSetParams& params) const = 0;

		[[nodiscard("unnecessary call")]] virtual service::AwaitableScalar<int> getIndex(service::FieldParams&& params) const = 0;
		[[nodiscard("unnecessary call")]] virtual service::AwaitableScalar<response::IdType> getRemoved(service::FieldParams&& params) const = 0;
	};

	template <class T>
	struct [[nodiscard("unnecessary construction")]] Model final
		: Concept
	{
		explicit Model(std::shared_ptr<T> pimpl) noexcept
			: _pimpl { std::move(pimpl) }
		{
		}

		[[nodiscard("unnecessary call")]] service::AwaitableScalar<int> getIndex(service::FieldParams&& params) const override
		{
			if constexpr (methods::ItemRemovedHas::getIndexWithParams<T>)
			{
				return { _pimpl->getIndex(std::move(params)) };
			}
			else if constexpr (methods::ItemRemovedHas::getIndex<T>)
			{
				return { _pimpl->getIndex() };
			}
			else
			{
				throw service::unimplemented_method(R"ex(ItemRemoved::getIndex)ex");
			}
		}

		[[nodiscard("unnecessary call")]] service::AwaitableScalar<response::IdType> getRemoved(service::FieldParams&& params) const override
		{
			if constexpr (methods::ItemRemovedHas::getRemovedWithParams<T>)
			{
				return { _pimpl->getRemoved(std::move(params)) };
			}
			else if constexpr (methods::ItemRemovedHas::getRemoved<T>)
			{
				return { _pimpl->getRemoved() };
			}
			else
			{
				throw service::unimplemented_method(R"ex(ItemRemoved::getRemoved)ex");
			}
		}

		void beginSelectionSet(const service::SelectionSetParams& params) const override
		{
			if constexpr (methods::ItemRemovedHas::beginSelectionSet<T>)
			{
				_pimpl->beginSelectionSet(params);
			}
		}

		void endSelectionSet(const service::SelectionSetParams& params) const override
		{
			if constexpr (methods::ItemRemovedHas::endSelectionSet<T>)
			{
				_pimpl->endSelectionSet(params);
			}
		}

	private:
		const std::shared_ptr<T> _pimpl;
	};

	explicit ItemRemoved(std::unique_ptr<const Concept> pimpl) noexcept;

	// Unions which include this type
	friend ItemChange;

	template <class I>
	[[nodiscard("unnecessary call")]] static constexpr bool implements() noexcept
	{
		return implements::ItemRemovedIs<I>;
	}

	[[nodiscard("unnecessary call")]] service::TypeNames getTypeNames() const noexcept;
	[[nodiscard("unnecessary call")]] service::ResolverMap getResolvers() const noexcept;

	void beginSelectionSet(const service::SelectionSetParams& params) const override;
	void endSelectionSet(const service::SelectionSetParams& params) const override;

	const std::unique_ptr<const Concept> _pimpl;

public:
	template <class T>
	explicit ItemRemoved(std::shared_ptr<T> pimpl) noexcept
		: ItemRemoved { std::unique_ptr<const Concept> { std::make_unique<Model<T>>(std::move(pimpl)) } }
	{
	}

	[[nodiscard("unnecessary call")]] static constexpr std::string_view getObjectType() noexcept
	{
		return { R"gql(ItemRemoved)gql" };
	}
};

} // namespace graphql::mapi::object

#endif // ITEMREMOVEDOBJECT_H
